import { __decorate } from "tslib";
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';
import { MessageService } from './message-service';
const httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};
let StudentService = class StudentService {
    constructor(http, messageService) {
        this.http = http;
        this.messageService = messageService;
        this.studentsUrl = 'api/students'; // URL to web api
    }
    /** GET students from the server */
    getStudents() {
        return this.http.get(this.studentsUrl)
            .pipe(tap(students => this.log(`fetched students`)), catchError(this.handleError('getStudents', [])));
    }
    /** GET student by id. Return `undefined` when id not found */
    getStudentNo404(id) {
        const url = `${this.studentsUrl}/?id=${id}`;
        return this.http.get(url)
            .pipe(map(students => students[0]), // returns a {0|1} element array
        tap(h => {
            const outcome = h ? `fetched` : `did not find`;
            this.log(`${outcome} student id=${id}`);
        }), catchError(this.handleError(`getStudent id=${id}`)));
    }
    /** GET Student by id. Will 404 if id not found */
    getStudent(id) {
        const url = `${this.studentsUrl}/${id}`;
        return this.http.get(url).pipe(tap(_ => this.log(`fetched student id=${id}`)), catchError(this.handleError(`getStudent id=${id}`)));
    }
    /* GET students whose name contains search term */
    searchStudents(term) {
        if (!term.trim()) {
            // if not search term, return empty student array.
            return of([]);
        }
        return this.http.get(`api/students/?name=${term}`).pipe(tap(_ => this.log(`found students matching "${term}"`)), catchError(this.handleError('searchStudents', [])));
    }
    //////// Save methods //////////
    /** POST: add a new student to the server */
    addStudent(student) {
        return this.http.post(this.studentsUrl, student, httpOptions).pipe(tap((student) => this.log(`added student w/ id=${student.id}`)), catchError(this.handleError('addStudent')));
    }
    /** DELETE: delete the student from the server */
    deleteStudent(student) {
        const id = typeof student === 'number' ? student : student.id;
        const url = `${this.studentsUrl}/${id}`;
        return this.http.delete(url, httpOptions).pipe(tap(_ => this.log(`deleted student id=${id}`)), catchError(this.handleError('deleteStudent')));
    }
    /** PUT: update the student on the server */
    updateStudent(student) {
        return this.http.put(this.studentsUrl, student, httpOptions).pipe(tap(_ => this.log(`updated student id=${student.id}`)), catchError(this.handleError('updateStudent')));
    }
    /**
     * Handle Http operation that failed.
     * Let the app continue.
     * @param operation - name of the operation that failed
     * @param result - optional value to return as the observable result
     */
    handleError(operation = 'operation', result) {
        return (error) => {
            // TODO: send the error to remote logging infrastructure
            console.error(error); // log to console instead
            // TODO: better job of transforming error for user consumption
            this.log(`${operation} failed: ${error.message}`);
            // Let the app keep running by returning an empty result.
            return of(result);
        };
    }
    /** Log a studentService message with the MessageService */
    log(message) {
        this.messageService.add('StudentService: ' + message);
    }
};
StudentService.ctorParameters = () => [
    { type: HttpClient },
    { type: MessageService }
];
StudentService = __decorate([
    Injectable({
        providedIn: 'root'
    })
], StudentService);
export { StudentService };
